import React, { useEffect, useState } from 'react'
import './Banner.scss'
import modeling3d from '../../img/icons/3DModelingSkillsDesign-03.png'
import automationcontrol from '../../img/icons/AUTOMATIONENGINEERING-03.png'
import detailengineering from '../../img/icons/DetialEngineering-03.png'
import modulerskilleddesign from '../../img/icons/ModularDesigns-03.png'
import scrubingsystem from '../../img/icons/ScrubbingsSystem-03.png'
import softwaredevelopment from '../../img/icons/MESSoftware-03.png'
import { useLocation } from 'react-router-dom'

export default function Banner() {
    const location = useLocation();
    const [heading , setHeading] = useState('')

    console.log(location)
    const data1 = [
        {
            img: automationcontrol,
            title: 'Industrial Automation'
        },
        {
            img: softwaredevelopment,
            title: 'MES & Software'
        },
        {
            img: detailengineering,
            title: 'Detail Engineering'
        },
    ]
    const data2 = [
        {
            img: modulerskilleddesign,
            title: 'Modular Skids Design'
        },
        {
            img: modeling3d,
            title: '3D Modeling & Drafting'
        },
        {
            img: scrubingsystem,
            title: 'Scrubbing System'
        },
    ]

    useEffect(() => {
        if(location.pathname === '/') { 
            setHeading('AUTOMATION \nENGINEERING \nDRAFTING')
        }
        if(location.pathname === '/showcase') { 
            setHeading('AUTOMATION CONTROL')
        }
    },  [location.pathname])

    return (
        <div className="Banner">
            <div className="left">
                <span>{heading}</span>
            </div>
            <div className="mid">
                <div className="top">
                    {data1.map((box) => {
                        return <div className="box" key={box.title}>
                            <a href="/">
                                <img src={box.img} alt="" />
                                <span>{box.title}</span>
                            </a>
                        </div>
                    })}
                </div>
                <div className="bottom">
                    {data2.map((box) => {
                        return <div className="box" key={box.title}>
                            <a href="/">
                                <img src={box.img} alt="" />
                                <span>{box.title}</span>
                            </a>
                        </div>
                    })}
                </div>
            </div>
            <div className="right">
            </div>
        </div >
    )
}
