import React, { useEffect, useState } from 'react';
import './Sidebar.scss';
import logo from '../../img/logo.png';
import { useLocation } from 'react-router-dom';

export default function Sidebar() {
    const [links, setLinks] = useState([]);
    const [title , setTitle] = useState('')
    const location = useLocation();
    const SHOWCASE = [
        {
            name: 'INDUSTRIAL AUTOMATION',
            link: '/',
            id: 1
        },
        {
            name: '3D MODELING & PIPING',
            link: '/',
            id: 6
        },
        {
            name: 'DETAIL ENGINEERING',
            link: '/',
            id: 2
        },
        {
            name: 'CUSTOM DESIGN',
            link: '/',
            id: 3
        },
        {
            name: 'SCRUBBING SYSTEM',
            link: '/',
            id: 4
        },
        {
            name: 'COLUMN INTERNALS',
            link: '/',
            id: 5
        }
    ]
    const HOME = [
        {
            name: 'CONTROL CUSTOM INTEGRATOR',
            link: '/',
            id: 1
        },
        {
            name: 'MODULAR SKIDS MANUFACTURER',
            link: '/',
            id: 2
        },
        {
            name: 'ENGINEERING FIRM',
            link: '/',
            id: 3
        },
        {
            name: 'CHEMICAL MANUFACTURE PLANT',
            link: '/',
            id: 4
        },
        {
            name: 'SCRUBBING SYSTEM',
            link: '/',
            id: 5
        },
        {
            name: 'TESTIMONIALS',
            link: '/',
            id: 6
        }
    ]

    const SERVICES = [
        {
            name: 'INDUSTRIAL AUTOMATION',
            link: '/',
            id: 1
        },
        {
            name: 'MES & SOFTWARE',
            link: '/',
            id: 2
        },
        {
            name: 'DETAIL ENGINEERING',
            link: '/',
            id: 3
        },
        {
            name: 'MODULAR SKIDS DESIGN',
            link: '/',
            id: 4
        },
        {
            name: '3D MODELING & DRAFTING',
            link: '/',
            id: 5
        },
    ]


    useEffect(() => {
        if (location.pathname === '/') {
            setTitle('SUCCESS STORIES')
            setLinks(HOME)
        }
        else if (location.pathname === '/showcase') {
            setTitle('SHOWCASE')
            setLinks(SHOWCASE)
        }
        else if (location.pathname === '/services') {
            setTitle('SERVICES')
            setLinks(SERVICES)
        }

        // eslint-disable-next-line
    }, [location.pathname])
    return (
        <div className="Sidebar">
            {location.pathname === '/' ? <h2>SUCCESS STORIES</h2>: <h2>SERVICES</h2> }
            <div className="links">
                {links.map((link) => {
                    return <a href={link.link} key={link.id}><span className="span">{link.name}</span></a>
                })}

                {/* <a href="/"><span className="span">3D MODELING & PIPING</span></a>
                <a href="/"><span className="span">DETAIL ENGINEERING</span></a>
                <a href="/"><span className="span">CUSTOM DESIGN</span></a>
                <a href="/"><span className="span">SCRUBBING SYSTEM</span></a>
                <a href="/"><span className="span">COLUMN INTERNALS</span></a> */}
            </div>
            {location.pathname !== '/showcase' && <div className="showcase">
                <a href="/">
                    <span>SHOWCASE</span>
                </a>
            </div>}
            <div className="logo">
                <img src={logo} alt="" />
            </div>
            {location.pathname === '/showcase' && <div className="contact-details">
                <h3>ADDRESS</h3>
                <span>Plot No.17, Sr .No.13,
                    Near Bramha Chaitnya Society,
                    Vidnyan Nagar Road,
                    Bavdhan, Pune, Maharashtra - 411021, INDIA.</span>

                <h3>TELEPHONE</h3>
                <span>+ 91-8983002534,</span>
                <span>+ 91-8983008534,</span>
                <span>+ 91-8446008660</span>

                <h3>E-Mail</h3>
                <span>info@transtechprojects.com</span>
            </div>}
        </div>
    )
}
